<?php 	// Include the database configuration	include_once 'config.php';	// Start a session	session_start();	// Connect to the database - Informaton stored in config.php	$connection = mysqli_connect( DB_HOST, DB_USER, DB_PASS ) or die('ERROR: ' . mysqli_error($connection));	// Select the database	mysqli_select_db( $connection, DB_NAME ) or die('ERROR: ' . mysqli_error());?>